var Twiground={checkTwitter:function(){var a=document.location.host;if(a!="twitter.com"){window.alert("Ops, looks like you aren't at twitter.com");new Error(":(")}},createGlobalNavLink:function(){var c=$("#global-nav-twiground"),b=$("#page-outer");if(c){c.remove()}var a=$('<li id="global-nav-twiground"><a href="#">Twiground</a></li>').appendTo("#global-nav ul").click(function(){b.slideToggle("slow")})},reveal:function(){this.checkTwitter();this.createGlobalNavLink()}};Twiground.reveal();